mapper.select:
select m_id,food_code,f_name, f_price
from orders,food 
where food.r_code = orders.r_code and m_id = #{m_id,jdbcType=VARCHAR}

	select f_name,
      	   f_price,
      	   f_content
       from food,
       		restaurant
            where food.r_code = restaurant.r_code and food_code = #{food_code}

	
select r_name, o_address, m_phone, o_pay, f_name, o_req
	from member, orders, restaurant, food
	where restaurant.r_code = orders.r_code and restaurant.r_code = food.r_code and food.r_code = orders.r_code and member.m_id = #{m_id}
