mapper.update:
UPDATE food 
SET f_price = #{f_price}, f_name = #{f_name}, f_image = #{f_image}, f_menucode = #{f_menucode}, f_content = #{f_content}, r_code = #{r_code}
WHERE food_code = #{food_code}

mapper.select:
SELECT food_code, f_price, f_name, f_image, f_menucode, f_content, r_code
FROM food
WHERE food_code = #{food_code}

SELECT food_code, f_price, f_name, f_image, f_menucode, f_content
FROM food
WHERE r_code = #{r_code}
